	public static BigInteger factorial(int n) {
		BigInteger total = BigInteger.ONE;
		
		for (int i = 1; i <= n; ++i) {
			total = total.multiply(BigInteger.valueOf(i));
		}
		
		return total;
	}
	
	public static int countNodes(int k, int d) {
		if (d == 0 || k == 1) {
			return d + 1;
		}
		
		return (int) ((Math.pow(k, d + 1) - 1) / (k - 1)); // k^(d+1) / (k-1)
	}
	
